import { NavLink } from 'react-router-dom'
import User from './User/User';

const Users = (props) => {

props.setUsers(
    { id: '1', name: 'Kate', followed: true, status: 'I am a teacher', location: { city: 'Lviv', country: 'Ukraine' }, img: 'https://i.pinimg.com/originals/3e/2e/8c/3e2e8c6fa626636eb4e8bdfe78edab3b.jpg' },
        { id: '2', name: 'Nick', followed: true, status: 'I like the dog', location: { city: 'Kyiv', country: 'Ukraine' }, img: 'https://i.shgcdn.com/e1b5913b-0b86-415b-b970-c7869878690f/-/format/auto/-/preview/3000x3000/-/quality/lighter/' },
        { id: '3', name: 'Anna', followed: true, status: 'I like this world', location: { city: 'Lytsk', country: 'Ukraine' }, img: 'https://images.unsplash.com/photo-1544005313-94ddf0286df2?ixid=MnwxMjA3fDB8MHxzZWFyY2h8Mnx8cG9ydHJhaXR8ZW58MHx8MHx8&ixlib=rb-1.2.1&w=1000&q=80' },
        { id: '4', name: 'Alex', followed: true, status: 'Come to me', location: { city: 'Uzgorod', country: 'Ukraine' }, img: 'https://www.headshotsprague.com/wp-content/uploads/2019/07/Headshots_Prague-emotional-portrait-of-a-smiling-entrepreneur-1.jpg' },
        { id: '5', name: 'Maria Tovsoltanova', followed: true, status: 'stay strong', location: { city: 'Lviv', country: 'Ukraine' }, img: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRNI7iYwCFABeh1BSJnBcBmqYDZOeC7X7xY-w&usqp=CAU' },
        { id: '6', name: 'Alan', followed: false, status: 'keep calm and dance', location: { city: 'Kyiv', country: 'Ukraine' }, img: 'https://for-men.ua/img/subscribe_success_popup_bg.jpg' },
        { id: '7', name: 'Mickle', followed: false, status: 'be awesome', location: { city: 'Odessa', country: 'Ukraine' }, img: 'https://images.ctfassets.net/cnu0m8re1exe/4NtjsbnI3HqmF100pSCxzp/188c91591c74cfba0bfde8cd25b503a8/shutterstock_1114755518.jpg?fm=jpg&fl=progressive&w=660&h=433&fit=fill' },
)

    let usersElements = props.users.map(user => <User name={user.name} key={user.id} ava={user.img} id={user.id} followed={user.followed} follow={props.follow} unfollow={props.unfollow}  />)
    return (
        <div className={s.usersPage}>
            <div className={s.users}>
                <p className={s.title}>Users</p>
                <div className={s.items}>
                    <NavLink to="/users" activeClassName={s.active}>{usersElements}</NavLink>
                </div>
                <button className={s.button}>Show more</button>
            </div>
            <div className={s.userInfo}>
                userInfo
            </div>
        </div>
    );
}
export default Users
