import React from 'react';
import { connect } from 'react-redux'
import UserProfile from './UserProfile';
import * as axios from 'axios';
import {setUserProfile} from '../../../redux/users-reducer'

class UserProfileAPIContainer extends React.Component {
    componentDidMount() {
        axios.get(`https://social-network.samuraijs.com/api/1.0/profile/4`)
            .then(response => {
                this.props.setUserProfile( response.data );
            })
    }
    render() {
        return (
            <UserProfile {...this.props} profile={this.props.profile} />
        )
    }
}

let mapStateToProps = (state) => {
    return {
profile: state.usersPage.profile
    }
}

const UserProfileContainer = connect(mapStateToProps,
    {
        setUserProfile
    }
)(UserProfileAPIContainer)
export default UserProfileContainer
