import React from 'react'
import s from './Users.module.css';
import { NavLink } from 'react-router-dom'
import User from './User/User';
import * as axios from 'axios';

class Users extends React.Component {

    componentDidMount() {
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${this.props.currentPage}&count=${this.props.pageSize}`)
            .then(response => {
                this.props.setUsers(response.data.items);
                this.props.setTotalUsersCount(response.data.totalCount);
            })
    }

    onPageChanged = (pageNumber) => {
        this.props.setCurrentPage(pageNumber);
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${pageNumber}&count=${this.props.pageSize}`)
        .then(response => {
            this.props.setUsers(response.data.items);
        })
    }

    usersElements = this.props.users.map(
        user => <User name={user.name}
            key={user.id}
            ava={user.photos.small}
            id={user.id}
            followed={user.followed}
            follow={this.props.follow}
            unfollow={this.props.unfollow} />)
    render() {

        let pagesCount = Math.ceil(this.props.totalUsersCount / this.props.pageSize);
        let pages = [];
        for (let i = 1; i <= pagesCount; i++) {
            pages.push(i);
        }
        return  (
            <div className={s.usersPage}>
                <div className={s.users}>
                    <p className={s.title}>Users</p>
                    <div className={s.items}>
                        <NavLink to="/users" activeClassName={s.active}>{this.usersElements}</NavLink>
                    </div>
                    <div className={s.pages}>
                        {pages.map(p => {
                            return <span className={ this.props.currentPage === p && s.selectedPage} onClick={(e) =>{this.onPageChanged(p)}}>{p}</span>
                        })}
                    </div>
                </div>
                <div className={s.userInfo}>
                    userInfo
                </div>
            </div>
        );
    }
}
export default Users
