import React from 'react';
import { addMessageBodyCreator, updateNewMessageBodyCreator } from '../../redux/dialogs-reducer';
import StoreContext from '../../StoreContext';
import Dialogs from './Dialogs';






const DialogsContainer = () => {

    return (
        <StoreContext.Consumer>
            {
                (store) => {
                    let state = store.getState();
                    let addMessage = () => {
                        store.dispatch(addMessageBodyCreator());

                    }
                    let onMessageChange = (body) => {
                        store.dispatch(updateNewMessageBodyCreator(body));
                    }
                    return <Dialogs addMessageBodyCreator={addMessage}
                        updateNewMessageBodyCreator={onMessageChange}
                        newMessageBody={state.dialogsPage.newMessageBody}
                        dialogsPage={state.dialogsPage} />
                }
            }
        </StoreContext.Consumer>

    );
}

const SuperDialogsContainer = connect()

export default DialogsContainer
