const SET_AUTHUSER_DATA = 'SET_USER_DATA';

let initialState = {
    id: null,
    email: null,
    login: null,
    isFetching: false
}


const authReducer = (state = initialState, action) => {
    switch (action.type) {
        case SET_USER_DATA:
            return {
                ...state,
                ...action.data
            }

        default:
            return state;
    }
}

export const setUserData = (userId, userEmail, userLogin) => ({type: SET_USER_DATA ,data: { userId, userEmail, userLogin}});


export default authReducer;
