import s from './Users.module.css';
import { NavLink } from 'react-router-dom'
import User from './User/User';
import * as axios from 'axios';

class Users extends React.Component {
    getUsers = () => {
        if (this.props.users.length === 0) {
            axios.get("https://social-network.samuraijs.com/api/1.0/users")
                .then(response => {
                    this.props.setUsers(response.data.items);
                })
        }
    }

    usersElements = this.props.users.map(user => <User name={user.name} key={user.id} ava={user.photos.small} id={user.id} followed={user.followed} follow={props.follow} unfollow={props.unfollow} />)
    render() {
        return (
            <div className={s.usersPage}>
                <div className={s.users}>
                    <p className={s.title}>Users</p>
                    <div className={s.items}>
                        <NavLink to="/users" activeClassName={s.active}>{this.usersElements}</NavLink>
                    </div>
                    <button className={s.button}>Show more</button>
                </div>
                <div className={s.userInfo}>
                    userInfo
                </div>
            </div>
        );
    }
}
export default Users
